import React, { useState, useEffect } from "react";
import CodeEditor from "./components/common/CodeEditor";
import ThemeSwitcher from "./components/common/ThemeSwitcher";
import axios from "axios";
import "./styles.css";

const App = () => {
  const [theme, setTheme] = useState("light");
  const [code, setCode] = useState("");
  const [userId, setUserId] = useState("1"); // Задайте идентификатор пользователя (это должно быть изменено на текущего пользователя)
  const [lessonId, setLessonId] = useState("1"); // Задайте идентификатор урока

  useEffect(() => {
    document.body.className = `${theme}-theme`;
  }, [theme]);

  // Функция для отправки кода пользователя на бэкенд
  const handleCodeSubmit = async (code) => {
    // Здесь вы можете добавить логику проверки кода пользователя и обновления прогресса
    // Для примера мы увеличиваем прогресс на 10 каждый раз, когда код отправляется
    try {
      const response = await axios.post("http://localhost:5000/update_user_progress", {
        user_id: userId,
        lesson_id: lessonId,
        progress: 10,
      });
      console.log(response.data);
    } catch (error) {
      console.error("Ошибка при отправке кода:", error);
    }
  };

  return (
    <div>
      <ThemeSwitcher currentTheme={theme} onSwitch={setTheme} />
      <div className="editor-container">
        <CodeEditor theme={theme} code={code} onChange={setCode} onCodeSubmit={handleCodeSubmit} />
      </div>
    </div>
  );
};

export default App;
